<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <meta http-equiv="X-UA-Compatible" content="ie=edge">
    <link rel="icon" type="image/png" sizes="16x16" href="../assets/images/logo-icon-api.png">
    <title>Register</title>

    <!-- Auth css -->
    <link href="../css/auth.css" rel="stylesheet">

    <!-- Custom fonts for this template -->
    <link href="../mainstyle/fontawesome-free/css/all.min.css" rel="stylesheet">
    <link href="../mainstyle/simple-line-icons/css/simple-line-icons.css" rel="stylesheet" type="text/css">
    <link href="https://fonts.googleapis.com/css?family=Lato:300,400,700,300italic,400italic,700italic" rel="stylesheet" type="text/css">

    <link href="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" rel="stylesheet" id="bootstrap-css">

    <script src="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>
    <script src="//cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>
</head>

<body>
    <!------ Include the above in your HEAD tag ---------->

    <div class="wrapper fadeInDown">
        <div id="formContent">
            <!-- Tabs Titles -->

            <div class="d-flex flex-row-reverse">
                <div class="p-2"><a href="../index.php"><i class="fa fa-times" aria-hidden="true"></i></a></div>
            </div>

            <!-- Icon -->
            <div class="fadeIn first">
                <img class="rounded-circle" src="data:image/png;base64," id="icon" alt="User Icon" />
            </div>

            <!-- Login Form -->
            <form action="../main/register.php" method="POST" onsubmit="return Validate()" name="vform">
                <input type="text" id="username" class="fadeIn second" name="username" placeholder="Username">
                <input type="text" id="email" class="fadeIn second" name="email" placeholder="Email Address">
                <input type="password" id="password" class="fadeIn third" name="password" placeholder="Password">
                <input type="password" id="confirm_password" class="fadeIn third" name="confirm_password" placeholder="Confirm Password">
                <input type="hidden" id="status" class="fadeIn third" name="status" value="User">
                <input type="submit" class="fadeIn fourth" value="Sign Up">
            </form>

            <!-- Remind Passowrd -->
            <div id="formFooter">
                You have an account? <a class="underlineHover" href="login.php">Sign In</a><br>
                <!-- <a class="underlineHover" href="forgot-password.php">Forgot Password?</a> -->
            </div>

        </div>
    </div>
</body>

<script type="text/javascript">
    //GETTING ALL INPUT TEXT OPJECTS
    var email = document.forms["vform"]["email"];
    var password = document.forms["vform"]["password"];
    var confirm_password = document.forms["vform"]["confirm_password"];

    //SETTING ALL EVENT LISTENER
    email.addEventListener("click", emailVerify);
    password.addEventListener("click", passwordVerify);
    confirm_password.addEventListener("click", confirm_passwordVerify);

    function Validate() {
        if (email.value == "") {
            email.style.border = "1px solid red";
            return false;
        }
        if (password.value == "") {
            password.style.border = "1px solid red";
            return false;
        }
        if (confirm_password.value == "") {
            confirm_password.style.border = "1px solid red";
            return false;
        }

        if (password.value != confirm_password.value) {
            alert("Password does not match!!");
            return false;

        }
    }

    function emailVerify() {
        if (email.value == "") {
            email.style.border = "";
            return true;
        }

    }

    function passwordVerify() {
        if (password.value == "") {
            password.style.border = "";
            return true;
        }

    }

    function confirm_passwordVerify() {
        if (confirm_password.value == "") {
            confirm_password.style.border = "";
            return true;
        }

    }
</script>

</html>